    private void btnProcesarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnProcesarActionPerformed
        // Datos
        int nota1 = Integer.parseInt(txtNota1.getText());
        int nota2 = Integer.parseInt(txtNota2.getText());
        int nota3 = Integer.parseInt(txtNota3.getText());
        // Proceso
        PromedioService service = new PromedioService();
        int pr = service.promedio(nota1, nota2, nota3);
        String condicion = service.condicion(pr);
        // Reporte
        txtPromedio.setText("" + pr);
        txtCondicion.setText(condicion);
        this.setStatusEdit(false);
    }//GEN-LAST:event_btnProcesarActionPerformed

    private void btnLimpiarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnLimpiarActionPerformed
        this.setStatusEdit(true);
    }//GEN-LAST:event_btnLimpiarActionPerformed

