    private void setStatusEdit(boolean activo) {
        txtNota1.setEnabled(activo);
        txtNota2.setEnabled(activo);
        txtNota3.setEnabled(activo);
        btnProcesar.setEnabled(activo);
        btnLimpiar.setEnabled(!activo);
        if (activo) {
            txtNota1.setText("");
            txtNota2.setText("");
            txtNota3.setText("");
            txtPromedio.setText("");
            txtCondicion.setText("");
            txtNota1.requestFocus();
        }
    }

